"use strict";

var AmpersandView = require('ampersand-view');
var BaseView = require('./base-view');

var View = BaseView.extend({
});

describe('View', function () {
  beforeEach(function () {
    this.view = new View({
    });
  });
  
  it('extends ampersand-view', function () {
    expect(this.view).to.be.an.instanceof(AmpersandView);
  });
  
  it('incorporates .bindMethods() helper', function () {
    expect(this.view.bindMethods).to.be.a('function');
  });
});
  
