/*==========================================================================*\
  A Photo Variant (e.g Original/B&W/Sepia)
\*==========================================================================*/
"use strict";

var BaseModel = require('app/base/base-model');

module.exports = BaseModel.extend({
  // extraProperties: 'ignore',
  props: {
    'id': ['number', true, 0], // The color's ID
    'name': ['string', true, ''], // Original|Black and White|Sepia   
    
    // original|black|sepia: A string used in the image 
    // URLs to get correctly colored image
    "prefix": ['string', true, ''], 
  },
  
  derived: {
    short_name: {
      fn: function () {
        return this.prefix === 'black' ? 'B&W' : this.name;
      },
    },
  },
  
  // parse: function (data) {
  // },
  
});
