/*==========================================================================*\
  Collection for photo variants
\*==========================================================================*/
"use strict";

module.exports = require('app/base/base-collection').extend({
  indexes: ['prefix'],
  model: require('./color-model'),
  
  getDefaultId: function () {
    var color;
    for (var i = 0; i < this.length; i++) {
      color = this.at(i);
      if (color.prefix === 'original') return color.id;
    }
    return this.at(0).id;
  },
  getDefaultColor: function () {
    var color;
    for (var i = 0; i < this.length; i++) {
      color = this.at(i);
      if (color.prefix === 'original') return color;
    }
    return this.at(0);
  },
});
