/*==========================================================================*\
  A single event on the homepage
\*==========================================================================*/
"use strict";

var BaseView = require('app/base/base-view');

module.exports = BaseView.extend({
  template: require('app/templates').event['event-item'],
  
  bindings: {
    'model.password': {
      type: function (el, val) {
        this.el.classList.toggle('locked', val);
        var url = this.model.thumb_url || 'img/no-image.png';
        if (!this.model.display_image) url = this.model.thumb_blur_url || url;
        el.style.backgroundImage = 'url('+ url +')';
      },
      selector: '.bg',
    },
  },
});
