'use strict';

var HomePage = require('app/event/events-page');
var UnsubscribeMixin = require('./unsubscribe-mixin');
var UnsubscribePopup = require('app/misc-popup/unsubscribe-popup');

var app = require('app');

module.exports = HomePage.extend(UnsubscribeMixin, {

  initialize: function (ops) {
    var self = this;

    HomePage.prototype.initialize.apply(this, arguments);

    this.ready(function () {

      setTimeout(function () {

        self.popup(new UnsubscribePopup(ops));

      }, 1000);

    });

  }

});
