'use strict';

var app = require('app');
var axios = require('axios');

module.exports = {
  updateEmailSubscriptions: function () {

  },

  getEmailSubscriptions: function (eventId, prefId, email) {
    var apiUrl = app.apiUrl + 'events/' + eventId + '/subscribe/' + prefId;
    var params = {
      url: app.url,
      email: email
    };

    return axios.get(apiUrl, {
      params: params
    });
  }
};
