"use strict";

var app = require('app');
var Parent = require('app/models/base-model');

var CombinationModel = require('app/models/base-model').extend({
  props: {
    variants: 'array',
    options: 'array',
    addons: 'array',
  },
});

var CombinationsCollection = require('app/models/base-collection').extend({
  model: CombinationModel,
  
});

module.exports = Parent.extend({
  props: {
    id: 'number',
    add_to_product: ['boolean', true, false], // can be added to products
    bulk_pricing_type: ['string', false, ''],
  },
  
  collections: {
    combinations: CombinationsCollection,
  },
  
  derived: {
  },
  
  parse: function (data) {
    // Parent.parse.apply(this, arguments);
    return data;
  },
  
});
