/*==========================================================================*\
  Popup with side-wide notices 
\*==========================================================================*/
"use strict";

var app = require('app');
var BaseView = require('app/base/base-view');
var NoticeView = require('./notice');

module.exports = BaseView.extend({
  viewType: 'lightbox-notices',
  dictionary: 'notices',
  autoRender: true,
  template: require('app/templates').notice['lightbox-notices'],
  
  events: {
    'click .button.hide': 'hide',
  },
  
  render: function () {
    this.renderWithTemplate(this);
    this.renderCollection(app.notices, NoticeView, '.items');
  },
  
  hide: function () {
    app.view.hideNotices();
  },
  
});

