"use strict";

var BaseView = require('app/base/base-view');
var ItemView = require('./products-popup-item');

module.exports = BaseView.extend({
  template: require('app/templates').photo.form['products-popup'],
  
  initialize: function () {
    BaseView.prototype.initialize.apply(this, arguments);
    this.render();
  },
  
  events: {
  },
  
  render: function () {
    this.renderWithTemplate(this);
    this.renderCollection(this.collection, ItemView, '.options');
  },

});
