/*==========================================================================*\
  A photo's purchase addon in cart
\*==========================================================================*/
"use strict";

var BaseModel = require('app/base/base-model');

module.exports = BaseModel.extend({
  props: {
    id: 'number',
    name: ['string', true, ''],
    min: ['number', true, 0],
    max: ['number', true, 10000],
    international: ['boolean', true, false], // forced for foreign address
    criteria: 'array', // forced for foreign address
  },
  
  derived: {
    isStandard: {
      fn: function () {
        return this.id === 1 || this.collection.models.indexOf(this) === 0;
      },
    },
  },
});
