"use strict";

var app = require('app');

var Country = require('app/base/base-model').extend({
  props: {
    id: 'number',
    code: 'string',
    name: 'string',
  },
});

module.exports = require('app/base/base-collection').extend({
  url: app.apiUrl +'checkout/countries',
  model: Country,
  
  initialize: function () {
    this.fetch();
  },
});
