/*==========================================================================*\
  Module providing common DOM operations
  as well as raf-based read/write
\*==========================================================================*/
"use strict";

var schedule = require('app/utils/schedule');

module.exports = {
  scheduleRead: function scheduleRead(fn, context) {
    return schedule(fn, 'highest', context);
  },
  scheduleWrite: function scheduleWrite(fn, priority, context) {
    return schedule(fn, priority, context);
  },
  unschedule: schedule.unschedule,
};
