/*==========================================================================*\
  A single product on the event't products listing
\*==========================================================================*/
"use strict";
var app = require('app');
var Parent = require('app/views/base-view');
var PopupView = require('./product-popup');

module.exports = Parent.extend({
  template: require('app/templates').event['product-item'],
  
  initialize: function () {
    Parent.prototype.initialize.apply(this, arguments);
  },
  
  events: {
    click: 'onClick',
  },
  
  onClick: function (e) {
    if (e && e.preventDefault) e.preventDefault();
    
    var popup = new PopupView({
      model: this.model,
      eventId: this.parent.parent.parent.eventId,
    });
    
    document.body.insertBefore(popup.el, document.getElementById('popup'));
    // app.view.overlays.appendChild(popup.el);
    
    return false;
  },
});
