/*==========================================================================*\
  Event's Product
\*==========================================================================*/
"use strict";

var app = require('app');
var Parent = require('app/views/base');
var PhotosView = require('app/views/event/photos');
var language = require('app/language');

module.exports = Parent.extend({
  
  template: require('app/templates').events.product,
  
  preview: false, // whether this is a preview view on 'All Categories' page
  
  initialize: function (ops) {
    Parent.prototype.initialize.apply(this, arguments);
    ops = ops || {};
    this.preview = !!ops.preview;
  },
  
  events: {
    'click .product-slideshow': 'onSlideshowClick',
  },
  
  bindings: {
    'model.name': '[data-hook=product-name]',
    'model.prod_list.length': {
      type: 'booleanClass',
      selector: '.event-product',
      yes: 'loaded',
      no: 'loading',
    },
    'model.prod_list': {
      type: function (el, list) {
        el.setAttribute('href', list.parent.app_url +'/'+ list.at(0).id);
      },
      selector: '.product-slideshow',
    },
  },
  
  render: function () {
    this.scheduleWrite(function () {
      this.renderWithTemplate(this);
      this.photos = new PhotosView({
        el: this.query('.photos'),
        collection: this.model.prod_list,
        parent: this,
        preview: this.preview,
        language: language,
      });
    });
  },
  
  onPageShow: function () {
    if (this.photos && this.photos.collection && this.photos.collection.length) {
      console.log('on show');
      this.showMore();
      this.photos.loadPhotos();
    }
  },
  
  onSlideshowClick: function () {
    app.router.enterSlideshow = true;
  },
  
  showMore: function () {
    this.photos.showMore();
    return this;
  },
  
});






