/*==========================================================================*\
  A purchase option in the buy form
\*==========================================================================*/
"use strict";

var Parent = require('app/views/base-view');
var OptionView = require('./addon-option');

module.exports = Parent.extend({
  template: require('app/templates').lightbox['buy-form']['addon-options'],
  
  initialize: function () {
    Parent.prototype.initialize.apply(this, arguments);
    this.render();
  },
  
  events: {
    'click': 'onClick',
  },
  
  render: function () {
    this.renderWithTemplate(this);
    this.renderCollection(this.collection, OptionView, '.options');
  },

});
