/*==========================================================================*\
  A purchase option in the buy form
\*==========================================================================*/
"use strict";

var Parent = require('app/views/base-view');
var ItemView = require('./products-popup-item');

module.exports = Parent.extend({
  template: require('app/templates').lightbox['buy-form']['products-popup'],
  
  initialize: function () {
    Parent.prototype.initialize.apply(this, arguments);
    this.render();
  },
  
  events: {
    'click': 'onClick',
  },
  
  render: function () {
    this.renderWithTemplate(this);
    this.renderCollection(this.collection, ItemView, '.options');
  },

});
